pragma solidity ^0.8.29;

import {
    UserOperationLib
} from "@account-abstraction/contracts/core/UserOperationLib.sol";
import {
    PackedUserOperation
} from "@account-abstraction/contracts/interfaces/PackedUserOperation.sol";
import {InvalidValidatorData} from "../interfaces/Errors.sol";

library PaymasterLib {
    using UserOperationLib for PackedUserOperation;

    uint256 private constant PAYMASTER_VALID_AFTER_END =
        UserOperationLib.PAYMASTER_DATA_OFFSET + 6;
    uint256 private constant PAYMASTER_VALID_UNTIL_END =
        PAYMASTER_VALID_AFTER_END + 6;

    function extractValidatorData(
        PackedUserOperation calldata userOp
    ) internal pure returns (uint48 validAfter, uint48 validUntil) {
        require(
            userOp.paymasterAndData.length >= PAYMASTER_VALID_UNTIL_END,
            InvalidValidatorData()
        );
        validAfter = uint48(
            bytes6(
                userOp.paymasterAndData[
                    UserOperationLib
                        .PAYMASTER_DATA_OFFSET:PAYMASTER_VALID_AFTER_END
                ]
            )
        );
        validUntil = uint48(
            bytes6(
                userOp.paymasterAndData[
                    PAYMASTER_VALID_AFTER_END:PAYMASTER_VALID_UNTIL_END
                ]
            )
        );
    }

    function extractPaymaster(
        PackedUserOperation calldata userOp
    ) internal pure returns (address paymaster) {
        return
            address(
                bytes20(
                    userOp.paymasterAndData[
                        :UserOperationLib.PAYMASTER_VALIDATION_GAS_OFFSET
                    ]
                )
            );
    }

    function calculateRequiredPrefund(
        PackedUserOperation calldata userOp
    ) internal pure returns (uint256 requiredPrefund) {
        unchecked {
            uint256 callGasLimit = userOp.unpackCallGasLimit();
            uint256 verificationGasLimit = userOp.unpackVerificationGasLimit();
            uint256 paymasterVerificationGasLimit = userOp
                .unpackPaymasterVerificationGasLimit();
            uint256 paymasterPostOpGasLimit = userOp.unpackPostOpGasLimit();
            uint256 maxFeePerGas = userOp.unpackMaxFeePerGas();
            uint256 requiredGas = verificationGasLimit +
                callGasLimit +
                paymasterVerificationGasLimit +
                paymasterPostOpGasLimit +
                userOp.preVerificationGas;

            requiredPrefund = requiredGas * maxFeePerGas;
        }
    }

    // TODO: harbour is userOp.sender and therefore part of userOpHash and could be removed
    function computeValidatorConfirmationHash(
        address harbour,
        bytes32 userOpHash
    ) internal view returns (bytes32 validatorConfirmationHash) {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(uint256 chainId,address verifyingContract)"
                ),
                block.chainid,
                address(this)
            )
        );
        bytes32 structHash = keccak256(
            abi.encode(
                keccak256(
                    "ValidatorConfirmation(address harbour,bytes32 userOpHash)"
                ),
                harbour,
                userOpHash
            )
        );
        validatorConfirmationHash = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
    }
}
","keccak256":"0xecceddd127a02ee50e3c5cf3e85645308bedefc1ecda0ee282ea4de7069997df","license":"GPL-3.0-only"},"src/mixins/ERC4337Mixin.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {IAccount} from "@account-abstraction/contracts/interfaces/IAccount.sol";
import {
    IEntryPoint
} from "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import {
    PackedUserOperation
} from "@account-abstraction/contracts/interfaces/PackedUserOperation.sol";
import {
    _packValidationData
} from "@account-abstraction/contracts/core/Helpers.sol";
import {
    UserOperationLib
} from "@account-abstraction/contracts/core/UserOperationLib.sol";
import {
    InvalidEntryPoint,
    InvalidTarget,
    SignerAlreadySignedTransaction,
    UnexpectedNonce,
    InvalidUserOpPaymaster,
    UnexpectedSafeTxHash,
    UnexpectedSigner
} from "../interfaces/Errors.sol";
import {IHarbourStore} from "../interfaces/HarbourStore.sol";
import {PaymasterLib} from "../libs/PaymasterLib.sol";
import {CoreLib} from "../libs/CoreLib.sol";

struct ERC4337MixinConfig {
    address entryPoint;
}

abstract contract ERC4337Mixin is IAccount, IHarbourStore {
    using UserOperationLib for PackedUserOperation;
    using PaymasterLib for PackedUserOperation;

    // ------------------------------------------------------------------
    // 4337 functions
    // ------------------------------------------------------------------

    /**
     * @notice The address of the EntryPoint contract supported by this module.
     */
    address public immutable SUPPORTED_ENTRYPOINT;

    constructor(ERC4337MixinConfig memory _config) {
        SUPPORTED_ENTRYPOINT = _config.entryPoint;
    }

    /**
     * @notice Return the nonce for a specific signer.
     * @param signer Address of the signer of the Safe transaction.
     * @return Nonce for the signer
     */
    function getNonce(address signer) public view virtual returns (uint256) {
        return
            IEntryPoint(SUPPORTED_ENTRYPOINT).getNonce(
                address(this),
                uint192(uint160(signer))
            );
    }

    function validateUserOp(
        PackedUserOperation calldata userOp,
        bytes32,
        uint256
    ) external view override returns (uint256 validationData) {
        require(
            msg.sender == SUPPORTED_ENTRYPOINT,
            InvalidEntryPoint(msg.sender)
        );

        require(
            bytes4(userOp.callData) == this.storeTransaction.selector,
            InvalidTarget(bytes4(userOp.callData))
        );
        (
            bytes32 safeTxHash,
            address signer,
            bytes32 r,
            bytes32 vs
        ) = _verifySafeTxData(userOp.callData[4:]);

        // --- DUPLICATE TRANSACTION SIGNATURE CHECK ---
        // Revert if this signer has already submitted *any* signature for this *exact* safeTxHash
        require(
            !_signatureStored(keccak256(abi.encodePacked(r, vs))),
            SignerAlreadySignedTransaction(signer, safeTxHash)
        );

        _verifySignature(safeTxHash, signer, r, vs);

        require(
            uint192(userOp.nonce >> 64) == uint192(uint160(signer)),
            UnexpectedNonce(signer)
        );

        // We skip the check that missingAccountFunds should be == 0, as this is the job of the entry point

        // Harbour can only be used with a paymaster when using 4337
        require(userOp.paymasterAndData.length > 0, InvalidUserOpPaymaster());
        return _packValidationData(false, 0, 0);
    }

    function _verifySafeTxData(
        bytes calldata callData
    ) private pure returns (bytes32, address, bytes32, bytes32) {
        (
            bytes32 safeTxHash,
            address safeAddress,
            uint256 chainId,
            uint256 nonce,
            address to,
            uint256 value,
            bytes memory data,
            uint8 operation,
            uint256 safeTxGas,
            uint256 baseGas,
            uint256 gasPrice,
            address gasToken,
            address refundReceiver,
            address signer,
            bytes32 r,
            bytes32 vs
        ) = abi.decode(
                callData,
                (
                    bytes32,
                    address,
                    uint256,
                    uint256,
                    address,
                    uint256,
                    bytes,
                    uint8,
                    uint256,
                    uint256,
                    uint256,
                    address,
                    address,
                    address,
                    bytes32,
                    bytes32
                )
            );
        bytes32 computedSafeTxHash = CoreLib.computeSafeTxHash(
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        require(
            computedSafeTxHash == safeTxHash,
            UnexpectedSafeTxHash(computedSafeTxHash)
        );
        // The computed length when properly encoded is based on the data length and the number of params
        // 4 bytes selector + 15 params each 32 bytes + 32 bytes offset of data + 32 bytes length of data + data length + 32 bytes buffer for padding
        return (safeTxHash, signer, r, vs);
    }

    function _verifySignature(
        bytes32 safeTxHash,
        address signer,
        bytes32 r,
        bytes32 vs
    ) private pure {
        (address recoveredSigner) = CoreLib.recoverSigner(safeTxHash, r, vs);
        require(signer == recoveredSigner, UnexpectedSigner(signer));
    }

    function storeTransaction(
        bytes32 safeTxHash,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        address signer,
        bytes32 r,
        bytes32 vs
    ) external returns (uint256 listIndex) {
        require(
            msg.sender == SUPPORTED_ENTRYPOINT,
            InvalidEntryPoint(msg.sender)
        );
        _storeTransaction(
            safeTxHash,
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );
        return
            _storeSignature(
                signer,
                safeAddress,
                chainId,
                nonce,
                safeTxHash,
                r,
                vs
            );
    }
}
","keccak256":"0xaedfc7c1ee2477f103258a29e0ef7812b5411e11c34cd770b6980b3df5dca1ee","license":"GPL-3.0-only"},"src/mixins/QuotaMixin.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IQuotaManager} from "../interfaces/QuotaManager.sol";
import {CoreLib} from "../libs/CoreLib.sol";
import {
    WithdrawalAlreadyPerformed,
    InsufficientTokensForWithdrawal,
    TokensInUse,
    QuotaOverflow
} from "../interfaces/Errors.sol";

struct QuotaStats {
    uint96 tokenBalance; // uint96 might also be enough, as this would be more tokens than most project have in circulation
    uint96 usedQuota; // Quota is limited to 96 bits (current Quota == Bytes of data or eth costs for a tx, so that should be ok)
    uint48 nextQuotaReset; // timestamps are Safe to limit to 64 bits
}

struct QuotaMixinConfig {
    uint32 timeframeQuotaReset;
    uint16 requiredQuotaMultiplier;
    uint96 maxAvailableQuota;
    address feeToken;
    uint32 quotaPerFeeToken;
    uint8 quotaPerFeeTokenScale;
}

abstract contract QuotaMixin is IQuotaManager {
    using SafeERC20 for IERC20;

    event Withdraw(address indexed signer, uint256 indexed amount);
    event Deposit(address indexed signer, uint256 indexed amount);

    mapping(address => QuotaStats) public quotaStatsForSigner;
    mapping(address => mapping(bytes32 => uint256)) public withdrawsForSigner;

    bool public immutable QUOTA_ENABLED;
    uint32 public immutable TIMEFRAME_QUOTA_RESET;
    uint96 public immutable MAX_AVAILABLE_QUOTA;
    address public immutable FEE_TOKEN;
    uint32 public immutable QUOTA_PER_FEE_TOKEN;
    uint8 public immutable QUOTA_PER_FEE_TOKEN_SCALE;

    constructor(QuotaMixinConfig memory _config) {
        QUOTA_ENABLED = _config.feeToken != address(0);
        TIMEFRAME_QUOTA_RESET = _config.timeframeQuotaReset;
        MAX_AVAILABLE_QUOTA = _config.maxAvailableQuota;
        FEE_TOKEN = _config.feeToken;
        QUOTA_PER_FEE_TOKEN = _config.quotaPerFeeToken;
        QUOTA_PER_FEE_TOKEN_SCALE = _config.quotaPerFeeTokenScale;
    }

    function depositTokensForSigner(address signer, uint96 amount) public {
        // We don't update the nextQuotaReset this way depositing more tokens does not negatively affect the reset schedule
        // The reset schedule always starts from 0, therefore is always a multiple of the reset timeframe (unless the timeframe is changed)
        quotaStatsForSigner[signer].tokenBalance += amount;
        // TODO: check if we want to track total tokens locked (might be useful in a recovery case)
        IERC20(FEE_TOKEN).safeTransferFrom(msg.sender, address(this), amount);
        emit Deposit(signer, amount);
    }

    function computeWithdrawHash(
        uint256 amount,
        address beneficiary,
        uint256 nonce
    ) internal view returns (bytes32 withdrawHash) {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(uint256 chainId,address verifyingContract)"
                ),
                block.chainid,
                address(this)
            )
        );
        bytes32 structHash = keccak256(
            abi.encode(
                keccak256(
                    "WithdrawRequest(uint256 amount,address beneficiary,uint256 nonce)"
                ),
                amount,
                beneficiary,
                nonce
            )
        );
        withdrawHash = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
    }

    function widthdrawTokensForSigner(
        bytes calldata signature,
        uint96 amount,
        address beneficiary,
        uint256 nonce
    ) public payable {
        bytes32 withdrawHash = computeWithdrawHash(amount, beneficiary, nonce);
        (address signer, , ) = CoreLib.recoverSigner(withdrawHash, signature);
        // Check that withdrawal was not executed yet
        require(
            withdrawsForSigner[signer][withdrawHash] == 0,
            WithdrawalAlreadyPerformed(withdrawHash)
        );
        withdrawsForSigner[signer][withdrawHash] = block.timestamp;

        _withdrawSignerTokens(signer, beneficiary, amount, false);

        emit Withdraw(signer, amount);
    }

    function _withdrawSignerTokens(
        address signer,
        address beneficiary,
        uint96 amount,
        bool skipResetCheck
    ) internal override {
        QuotaStats storage stats = quotaStatsForSigner[signer];
        require(
            stats.tokenBalance >= amount,
            InsufficientTokensForWithdrawal()
        );
        // We use the quota reset timeframe as a unlock timeframe
        // -> currently the signer is not allowed to sign any Safe transaction during this timeframe
        // TODO: have dedicated unlock logic (also to avoid some fee exploit flows)
        require(
            skipResetCheck || stats.nextQuotaReset < block.timestamp,
            TokensInUse()
        );

        stats.tokenBalance -= amount;

        if (beneficiary != address(this)) {
            _transferFeeToken(beneficiary, amount);
        }
    }

    function _transferFeeToken(
        address beneficiary,
        uint96 amount
    ) internal override {
        IERC20(FEE_TOKEN).safeTransfer(beneficiary, amount);
    }

    function availableFreeQuotaForSigner(
        address signer
    )
        public
        view
        returns (
            uint96 availableFreeQuota,
            uint96 usedSignerQuota,
            uint48 nextSignerQuotaReset
        )
    {
        QuotaStats memory stats = quotaStatsForSigner[signer];
        nextSignerQuotaReset = stats.nextQuotaReset;
        if (nextSignerQuotaReset > block.timestamp) {
            usedSignerQuota = stats.usedQuota;
        } else {
            // Signer quota should be reset (therefore be 0)
            usedSignerQuota = 0;
            // The reset time should always be aligned with the timeframe (be a multiple)
            // First the time difference since the last reset is calculated (last reset - block time)
            // Then the elablesed time in the current timeframe (modulo with timeframe duration)
            // Then substract this from the current blocktime to get the start of the current timeframe
            // And lastly add the timeframe duration to get the starting point of the next timeframe
            uint48 blocktime = uint48(block.timestamp);
            nextSignerQuotaReset =
                blocktime -
                ((blocktime - nextSignerQuotaReset) % TIMEFRAME_QUOTA_RESET) +
                TIMEFRAME_QUOTA_RESET;
        }
        // We cast tokenBalance to uint256 to use more bits for the arithmetics
        uint256 maxSignerQuota = (uint256(stats.tokenBalance) *
            QUOTA_PER_FEE_TOKEN) / 10 ** QUOTA_PER_FEE_TOKEN_SCALE;

        require(
            maxSignerQuota <= type(uint96).max,
            QuotaOverflow(maxSignerQuota)
        );

        uint96 freeSignerQuota = uint96(maxSignerQuota);
        // If MAX_AVAILABLE_QUOTA is set to 0 then there is no limit
        if (MAX_AVAILABLE_QUOTA > 0 && freeSignerQuota > MAX_AVAILABLE_QUOTA) {
            freeSignerQuota = MAX_AVAILABLE_QUOTA;
        }
        if (usedSignerQuota <= freeSignerQuota) {
            availableFreeQuota = freeSignerQuota - usedSignerQuota;
        } else {
            availableFreeQuota = 0;
        }
    }

    function _updateQuotaParams(
        address signer,
        uint96 newSignerQuota,
        uint48 nextSignerQuotaReset
    ) internal {
        QuotaStats storage stats = quotaStatsForSigner[signer];
        if (nextSignerQuotaReset != stats.nextQuotaReset) {
            stats.nextQuotaReset = nextSignerQuotaReset;
        }
        stats.usedQuota = newSignerQuota;
    }

    function _checkAndUpdateQuota(
        address signer,
        uint256 requiredSignerQuota
    ) internal override returns (bool) {
        if (!QUOTA_ENABLED || requiredSignerQuota == 0) return true;
        (
            uint96 availableFreeSignerQuota,
            uint96 usedSignerQuota,
            uint48 nextSignerQuotaReset
        ) = availableFreeQuotaForSigner(signer);
        if (requiredSignerQuota > availableFreeSignerQuota) return false;
        // Casting to uint64 is safe, as availableFreeSignerQuota is at most a uint64 and we compare it against that
        _updateQuotaParams(
            signer,
            usedSignerQuota + uint96(requiredSignerQuota),
            nextSignerQuotaReset
        );
        return true;
    }
}
