// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

/**
 * @title Safe Secret Harbour Registration Key
 * @dev This registration key is used internally as a mapping key to transaction registration block
 *      numbers. It allows enumeration of block numbers where Safe transactions for a given chain,
 *      Safe, and nonce were registered by a notary.
 */
library RegistrationKey {
    type T is bytes32;

    /**
     * @notice Compute the registeration key for a given `(chainId, safe, nonce, signer)` tuple.
     */
    function get(
        uint256 chainId,
        address safe,
        uint256 nonce,
        address notary
    ) internal pure returns (T key) {
        // uid = keccak256(chainId, safe, nonce, notary)
        // solhint-disable-next-line no-inline-assembly
        assembly ("memory-safe") {
            let ptr := mload(0x40)
            mstore(ptr, chainId)
            mstore(add(ptr, 32), safe)
            mstore(add(ptr, 64), nonce)
            mstore(add(ptr, 96), notary)
            key := keccak256(ptr, 128)
        }
    }

    /**
     * @notice Computes an opaque unique identifier for the specified registration key and index.
     *
     * @dev This UID is indexed in transaction registration events, and can be used to an RPC node
     *      for Ethereum logs for a specific transaction registration. This allows the harbour
     *      contract to be used without event indexing with RPC nodes that do not support queries
     *      over large block ranges.
     */
    function uniqueIdentifier(
        T self,
        uint256 index
    ) internal pure returns (bytes32 uid) {
        // uid = keccak256(self, index)
        // solhint-disable-next-line no-inline-assembly
        assembly ("memory-safe") {
            mstore(0, self)
            mstore(32, index)
            uid := keccak256(0, 64)
        }
    }
}

src/mixins/ERC4337Mixin.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {IAccount} from "@account-abstraction/contracts/interfaces/IAccount.sol";
import {
    IEntryPoint
} from "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import {
    PackedUserOperation
} from "@account-abstraction/contracts/interfaces/PackedUserOperation.sol";
import {
    _packValidationData
} from "@account-abstraction/contracts/core/Helpers.sol";
import {
    UserOperationLib
} from "@account-abstraction/contracts/core/UserOperationLib.sol";
import {
    InvalidEntryPoint,
    InvalidTarget,
    SignerAlreadySignedTransaction,
    UnexpectedNonce,
    InvalidUserOpPaymaster,
    UnexpectedSafeTxHash,
    UnexpectedSigner
} from "../interfaces/Errors.sol";
import {IHarbourStore} from "../interfaces/HarbourStore.sol";
import {PaymasterLib} from "../libs/PaymasterLib.sol";
import {CoreLib} from "../libs/CoreLib.sol";

struct ERC4337MixinConfig {
    address entryPoint;
}

abstract contract ERC4337Mixin is IAccount, IHarbourStore {
    using UserOperationLib for PackedUserOperation;
    using PaymasterLib for PackedUserOperation;

    // ------------------------------------------------------------------
    // 4337 functions
    // ------------------------------------------------------------------

    /**
     * @notice The address of the EntryPoint contract supported by this module.
     */
    address public immutable SUPPORTED_ENTRYPOINT;

    constructor(ERC4337MixinConfig memory _config) {
        SUPPORTED_ENTRYPOINT = _config.entryPoint;
    }

    /**
     * @notice Return the nonce for a specific signer.
     * @param signer Address of the signer of the Safe transaction.
     * @return Nonce for the signer
     */
    function getNonce(address signer) public view virtual returns (uint256) {
        return
            IEntryPoint(SUPPORTED_ENTRYPOINT).getNonce(
                address(this),
                uint192(uint160(signer))
            );
    }

    function validateUserOp(
        PackedUserOperation calldata userOp,
        bytes32,
        uint256
    ) external view override returns (uint256 validationData) {
        require(
            msg.sender == SUPPORTED_ENTRYPOINT,
            InvalidEntryPoint(msg.sender)
        );

        require(
            bytes4(userOp.callData) == this.storeTransaction.selector,
            InvalidTarget(bytes4(userOp.callData))
        );
        (
            bytes32 safeTxHash,
            address signer,
            bytes32 r,
            bytes32 vs
        ) = _verifySafeTxData(userOp.callData[4:]);

        // --- DUPLICATE TRANSACTION SIGNATURE CHECK ---
        // Revert if this signer has already submitted *any* signature for this *exact* safeTxHash
        require(
            !_signatureStored(keccak256(abi.encodePacked(r, vs))),
            SignerAlreadySignedTransaction(signer, safeTxHash)
        );

        _verifySignature(safeTxHash, signer, r, vs);

        require(
            uint192(userOp.nonce >> 64) == uint192(uint160(signer)),
            UnexpectedNonce(signer)
        );

        // We skip the check that missingAccountFunds should be == 0, as this is the job of the entry point

        // Harbour can only be used with a paymaster when using 4337
        require(userOp.paymasterAndData.length > 0, InvalidUserOpPaymaster());
        return _packValidationData(false, 0, 0);
    }

    function _verifySafeTxData(
        bytes calldata callData
    ) private pure returns (bytes32, address, bytes32, bytes32) {
        (
            bytes32 safeTxHash,
            address safeAddress,
            uint256 chainId,
            uint256 nonce,
            address to,
            uint256 value,
            bytes memory data,
            uint8 operation,
            uint256 safeTxGas,
            uint256 baseGas,
            uint256 gasPrice,
            address gasToken,
            address refundReceiver,
            address signer,
            bytes32 r,
            bytes32 vs
        ) = abi.decode(
                callData,
                (
                    bytes32,
                    address,
                    uint256,
                    uint256,
                    address,
                    uint256,
                    bytes,
                    uint8,
                    uint256,
                    uint256,
                    uint256,
                    address,
                    address,
                    address,
                    bytes32,
                    bytes32
                )
            );
        bytes32 computedSafeTxHash = CoreLib.computeSafeTxHash(
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        require(
            computedSafeTxHash == safeTxHash,
            UnexpectedSafeTxHash(computedSafeTxHash)
        );
        // The computed length when properly encoded is based on the data length and the number of params
        // 4 bytes selector + 15 params each 32 bytes + 32 bytes offset of data + 32 bytes length of data + data length + 32 bytes buffer for padding
        return (safeTxHash, signer, r, vs);
    }

    function _verifySignature(
        bytes32 safeTxHash,
        address signer,
        bytes32 r,
        bytes32 vs
    ) private pure {
        (address recoveredSigner) = CoreLib.recoverSigner(safeTxHash, r, vs);
        require(signer == recoveredSigner, UnexpectedSigner(signer));
    }

    function storeTransaction(
        bytes32 safeTxHash,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        address signer,
        bytes32 r,
        bytes32 vs
    ) external returns (uint256 listIndex) {
        require(
            msg.sender == SUPPORTED_ENTRYPOINT,
            InvalidEntryPoint(msg.sender)
        );
        _storeTransaction(
            safeTxHash,
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );
        return
            _storeSignature(
                signer,
                safeAddress,
                chainId,
                nonce,
                safeTxHash,
                r,
                vs
            );
    }
}

src/mixins/QuotaMixin.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IQuotaManager} from "../interfaces/QuotaManager.sol";
import {CoreLib} from "../libs/CoreLib.sol";
import {
    WithdrawalAlreadyPerformed,
    InsufficientTokensForWithdrawal,
    TokensInUse,
    QuotaOverflow
} from "../interfaces/Errors.sol";

struct QuotaStats {
    uint96 tokenBalance; // uint96 might also be enough, as this would be more tokens than most project have in circulation
    uint96 usedQuota; // Quota is limited to 96 bits (current Quota == Bytes of data or eth costs for a tx, so that should be ok)
    uint48 nextQuotaReset; // timestamps are Safe to limit to 64 bits
}

struct QuotaMixinConfig {
    uint32 timeframeQuotaReset;
    uint16 requiredQuotaMultiplier;
    uint96 maxAvailableQuota;
    address feeToken;
    uint32 quotaPerFeeToken;
    uint8 quotaPerFeeTokenScale;
}

abstract contract QuotaMixin is IQuotaManager {
    using SafeERC20 for IERC20;

    event Withdraw(address indexed signer, uint256 indexed amount);
    event Deposit(address indexed signer, uint256 indexed amount);

    mapping(address => QuotaStats) public quotaStatsForSigner;
    mapping(address => mapping(bytes32 => uint256)) public withdrawsForSigner;

    bool public immutable QUOTA_ENABLED;
    uint32 public immutable TIMEFRAME_QUOTA_RESET;
    uint96 public immutable MAX_AVAILABLE_QUOTA;
    address public immutable FEE_TOKEN;
    uint32 public immutable QUOTA_PER_FEE_TOKEN;
    uint8 public immutable QUOTA_PER_FEE_TOKEN_SCALE;

    constructor(QuotaMixinConfig memory _config) {
        QUOTA_ENABLED = _config.feeToken != address(0);
        TIMEFRAME_QUOTA_RESET = _config.timeframeQuotaReset;
        MAX_AVAILABLE_QUOTA = _config.maxAvailableQuota;
        FEE_TOKEN = _config.feeToken;
        QUOTA_PER_FEE_TOKEN = _config.quotaPerFeeToken;
        QUOTA_PER_FEE_TOKEN_SCALE = _config.quotaPerFeeTokenScale;
    }

    function depositTokensForSigner(address signer, uint96 amount) public {
        // We don't update the nextQuotaReset this way depositing more tokens does not negatively affect the reset schedule
        // The reset schedule always starts from 0, therefore is always a multiple of the reset timeframe (unless the timeframe is changed)
        quotaStatsForSigner[signer].tokenBalance += amount;
        // TODO: check if we want to track total tokens locked (might be useful in a recovery case)
        IERC20(FEE_TOKEN).safeTransferFrom(msg.sender, address(this), amount);
        emit Deposit(signer, amount);
    }

    function computeWithdrawHash(
        uint256 amount,
        address beneficiary,
        uint256 nonce
    ) internal view returns (bytes32 withdrawHash) {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(uint256 chainId,address verifyingContract)"
                ),
                block.chainid,
                address(this)
            )
        );
        bytes32 structHash = keccak256(
            abi.encode(
                keccak256(
                    "WithdrawRequest(uint256 amount,address beneficiary,uint256 nonce)"
                ),
                amount,
                beneficiary,
                nonce
            )
        );
        withdrawHash = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
    }

    function widthdrawTokensForSigner(
        bytes calldata signature,
        uint96 amount,
        address beneficiary,
        uint256 nonce
    ) public payable {
        bytes32 withdrawHash = computeWithdrawHash(amount, beneficiary, nonce);
        (address signer, , ) = CoreLib.recoverSigner(withdrawHash, signature);
        // Check that withdrawal was not executed yet
        require(
            withdrawsForSigner[signer][withdrawHash] == 0,
            WithdrawalAlreadyPerformed(withdrawHash)
        );
        withdrawsForSigner[signer][withdrawHash] = block.timestamp;

        _withdrawSignerTokens(signer, beneficiary, amount, false);

        emit Withdraw(signer, amount);
    }

    function _withdrawSignerTokens(
        address signer,
        address beneficiary,
        uint96 amount,
        bool skipResetCheck
    ) internal override {
        QuotaStats storage stats = quotaStatsForSigner[signer];
        require(
            stats.tokenBalance >= amount,
            InsufficientTokensForWithdrawal()
        );
        // We use the quota reset timeframe as a unlock timeframe
        // -> currently the signer is not allowed to sign any Safe transaction during this timeframe
        // TODO: have dedicated unlock logic (also to avoid some fee exploit flows)
        require(
            skipResetCheck || stats.nextQuotaReset < block.timestamp,
            TokensInUse()
        );

        stats.tokenBalance -= amount;

        if (beneficiary != address(this)) {
            _transferFeeToken(beneficiary, amount);
        }
    }

    function _transferFeeToken(
        address beneficiary,
        uint96 amount
    ) internal override {
        IERC20(FEE_TOKEN).safeTransfer(beneficiary, amount);
    }

    function availableFreeQuotaForSigner(
        address signer
    )
        public
        view
        returns (
            uint96 availableFreeQuota,
            uint96 usedSignerQuota,
            uint48 nextSignerQuotaReset
        )
    {
        QuotaStats memory stats = quotaStatsForSigner[signer];
        nextSignerQuotaReset = stats.nextQuotaReset;
        if (nextSignerQuotaReset > block.timestamp) {
            usedSignerQuota = stats.usedQuota;
        } else {
            // Signer quota should be reset (therefore be 0)
            usedSignerQuota = 0;
            // The reset time should always be aligned with the timeframe (be a multiple)
            // First the time difference since the last reset is calculated (last reset - block time)
            // Then the elablesed time in the current timeframe (modulo with timeframe duration)
            // Then substract this from the current blocktime to get the start of the current timeframe
            // And lastly add the timeframe duration to get the starting point of the next timeframe
            uint48 blocktime = uint48(block.timestamp);
            nextSignerQuotaReset =
                blocktime -
                ((blocktime - nextSignerQuotaReset) % TIMEFRAME_QUOTA_RESET) +
                TIMEFRAME_QUOTA_RESET;
        }
        // We cast tokenBalance to uint256 to use more bits for the arithmetics
        uint256 maxSignerQuota = (uint256(stats.tokenBalance) *
            QUOTA_PER_FEE_TOKEN) / 10 ** QUOTA_PER_FEE_TOKEN_SCALE;

        require(
            maxSignerQuota <= type(uint96).max,
            QuotaOverflow(maxSignerQuota)
        );

        uint96 freeSignerQuota = uint96(maxSignerQuota);
        // If MAX_AVAILABLE_QUOTA is set to 0 then there is no limit
        if (MAX_AVAILABLE_QUOTA > 0 && freeSignerQuota > MAX_AVAILABLE_QUOTA) {
            freeSignerQuota = MAX_AVAILABLE_QUOTA;
        }
        if (usedSignerQuota <= freeSignerQuota) {
            availableFreeQuota = freeSignerQuota - usedSignerQuota;
        } else {
            availableFreeQuota = 0;
        }
    }

    function _updateQuotaParams(
        address signer,
        uint96 newSignerQuota,
        uint48 nextSignerQuotaReset
    ) internal {
        QuotaStats storage stats = quotaStatsForSigner[signer];
        if (nextSignerQuotaReset != stats.nextQuotaReset) {
            stats.nextQuotaReset = nextSignerQuotaReset;
        }
        stats.usedQuota = newSignerQuota;
    }

    function _checkAndUpdateQuota(
        address signer,
        uint256 requiredSignerQuota
    ) internal override returns (bool) {
        if (!QUOTA_ENABLED || requiredSignerQuota == 0) return true;
        (
            uint96 availableFreeSignerQuota,
            uint96 usedSignerQuota,
            uint48 nextSignerQuotaReset
        ) = availableFreeQuotaForSigner(signer);
        if (requiredSignerQuota > availableFreeSignerQuota) return false;
        // Casting to uint64 is safe, as availableFreeSignerQuota is at most a uint64 and we compare it against that
        _updateQuotaParams(
            signer,
            usedSignerQuota + uint96(requiredSignerQuota),
            nextSignerQuotaReset
        );
        return true;
    }
}

src/mixins/SlashingMixin.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    PackedUserOperation
} from "@account-abstraction/contracts/interfaces/PackedUserOperation.sol";
import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IQuotaManager} from "../interfaces/QuotaManager.sol";
import {PaymasterLib} from "../libs/PaymasterLib.sol";
import {CoreLib} from "../libs/CoreLib.sol";
import {IERC4337InfoProvider} from "../interfaces/ERC4337.sol";
import {ISlashingCondition} from "../interfaces/Conditions.sol";
import {
    InsufficientTokensForWithdrawal,
    InvalidUserOpPaymaster,
    ConditionWasNotActive,
    ConditionAlreadyEnabled,
    ConditionAlreadyDisabled,
    ConditionNotEnabled,
    ConditionNotOffended,
    UserOpAlreadySlashed,
    NothingToSlash,
    InvalidBeneficiary
} from "../interfaces/Errors.sol";

struct SlashingMixinConfig {
    uint48 enableCoditionsDelay;
    ISlashingCondition[] initialConditions;
}

abstract contract SlashingMixin is IQuotaManager, IERC4337InfoProvider {
    using SafeERC20 for IERC20;
    using PaymasterLib for PackedUserOperation;

    struct ConditionStatus {
        uint48 enabledAfter;
        uint48 enabledUntil;
    }

    uint48 public immutable ENABLE_CONDITIONS_DELAY;

    event Slashed(
        ISlashingCondition indexed offendingCondition,
        address indexed validator,
        uint256 indexed amount
    );

    event EnableCondition(
        ISlashingCondition indexed offendingCondition,
        uint48 enabledFrom
    );

    event DisableCondition(
        ISlashingCondition indexed offendingCondition,
        uint48 enabledUntil
    );

    uint256 public slashedTokens;
    mapping(ISlashingCondition => ConditionStatus) public enabledConditions;
    mapping(bytes32 => uint256) public slashedUserOps;

    constructor(SlashingMixinConfig memory _config) {
        ENABLE_CONDITIONS_DELAY = _config.enableCoditionsDelay;
        for (uint256 i = 0; i < _config.initialConditions.length; i++) {
            enabledConditions[_config.initialConditions[i]] = ConditionStatus({
                enabledAfter: uint48(block.timestamp),
                enabledUntil: 0
            });
        }
    }

    function slashValidator(
        ISlashingCondition offendingCondition,
        PackedUserOperation calldata userOp
    ) public {
        // Only slashing for userOps related to this paymaster is allowed
        require(
            userOp.extractPaymaster() == address(this),
            InvalidUserOpPaymaster()
        );
        (uint256 validAfter, uint256 validUntil) = userOp
            .extractValidatorData();
        ConditionStatus memory conditionStatus = enabledConditions[
            offendingCondition
        ];
        require(
            (conditionStatus.enabledAfter < validUntil || validUntil == 0) &&
                (validAfter < conditionStatus.enabledUntil ||
                    conditionStatus.enabledUntil == 0),
            ConditionWasNotActive()
        );
        bytes32 userOpHash = getSupportedEntrypoint().getUserOpHash(userOp);
        bytes32 digest = PaymasterLib.computeValidatorConfirmationHash(
            userOp.sender,
            userOpHash
        );
        (address validator, , ) = CoreLib.recoverSigner(
            digest,
            userOp.signature
        );
        require(
            offendingCondition.shouldBeSlashed(validator, userOp),
            ConditionNotOffended()
        );
        require(slashedUserOps[userOpHash] == 0, UserOpAlreadySlashed());
        uint96 usedQuota = uint96(userOp.calculateRequiredPrefund());
        uint96 slashingAmount = _adjustSlashingAmount(validator, usedQuota);
        require(slashingAmount > 0, NothingToSlash());
        // Currently only the slashed amount is stored, more information could be stored if necessary
        slashedUserOps[userOpHash] = slashingAmount;
        slashedTokens += slashingAmount;
        // By withdrawing the slashed tokens to the slashing contract they can be transferred later
        _withdrawSignerTokens(validator, address(this), slashingAmount, true);
        emit Slashed(offendingCondition, validator, slashingAmount);
    }

    // Can be used to adjust slashing amount, i.e. based on quota relation
    function _adjustSlashingAmount(
        address validator,
        uint96 slashingAmount
    ) internal virtual returns (uint96);

    function _enableCondition(ISlashingCondition condition) internal {
        require(
            enabledConditions[condition].enabledAfter == 0,
            ConditionAlreadyEnabled()
        );
        uint48 enabledAfter = uint48(block.timestamp) + ENABLE_CONDITIONS_DELAY;
        enabledConditions[condition] = ConditionStatus({
            enabledAfter: enabledAfter,
            enabledUntil: 0
        });
        emit EnableCondition(condition, enabledAfter);
    }

    function _disableCondition(ISlashingCondition condition) internal {
        ConditionStatus memory conditionStatus = enabledConditions[condition];
        require(conditionStatus.enabledAfter != 0, ConditionNotEnabled());
        require(conditionStatus.enabledUntil == 0, ConditionAlreadyDisabled());
        uint48 enabledUntil = uint48(block.timestamp) + ENABLE_CONDITIONS_DELAY;
        enabledConditions[condition].enabledUntil = enabledUntil;
        emit EnableCondition(condition, enabledUntil);
    }

    function _withdrawSlashedTokens(
        address beneficiary,
        uint96 amount
    ) internal {
        require(beneficiary != address(this), InvalidBeneficiary());
        require(amount <= slashedTokens, InsufficientTokensForWithdrawal());
        unchecked {
            slashedTokens -= amount;
        }
        _transferFeeToken(beneficiary, amount);
    }
}

src/SafeHarbourPaymaster.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    BasePaymaster
} from "@account-abstraction/contracts/core/BasePaymaster.sol";
import {
    PackedUserOperation
} from "@account-abstraction/contracts/interfaces/PackedUserOperation.sol";
import {
    IEntryPoint
} from "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import {
    _packValidationData
} from "@account-abstraction/contracts/core/Helpers.sol";
import {QuotaMixin, QuotaMixinConfig} from "./mixins/QuotaMixin.sol";
import {SlashingMixin, SlashingMixinConfig} from "./mixins/SlashingMixin.sol";
import {ERC4337Mixin} from "./mixins/ERC4337Mixin.sol";
import {PaymasterLib} from "./libs/PaymasterLib.sol";
import {CoreLib} from "./libs/CoreLib.sol";
import {InvalidTarget, InvalidUserOpPaymaster} from "./interfaces/Errors.sol";

// TODO: do not use BasePaymaster as it is not optimized to our needs (i.e. no custom errors)
contract SafeHarbourPaymaster is BasePaymaster, QuotaMixin, SlashingMixin {
    using PaymasterLib for PackedUserOperation;

    constructor(
        address manager,
        IEntryPoint supportedEntrypoint,
        QuotaMixinConfig memory _quotaMixinconfig,
        SlashingMixinConfig memory _slashingMixinconfig
    )
        BasePaymaster(supportedEntrypoint)
        QuotaMixin(_quotaMixinconfig)
        SlashingMixin(_slashingMixinconfig)
    {
        transferOwnership(manager);
    }

    function getSupportedEntrypoint()
        public
        view
        override
        returns (IEntryPoint)
    {
        return entryPoint;
    }

    /**
     * Validate a user operation.
     * @param userOp     - The user operation.
     * @param maxCost    - The maximum cost of the user operation.
     */
    function _validatePaymasterUserOp(
        PackedUserOperation calldata userOp,
        bytes32 userOpHash,
        uint256 maxCost
    ) internal override returns (bytes memory context, uint256 validationData) {
        (context);

        // Theoretically this check is also performed by the 4337 mixin and could be skipped here (especially if the sender is trusted)
        require(
            bytes4(userOp.callData) == ERC4337Mixin.storeTransaction.selector,
            InvalidTarget(bytes4(userOp.callData))
        );

        require(
            userOp.extractPaymaster() == address(this),
            InvalidUserOpPaymaster()
        );

        (address validator, , ) = CoreLib.recoverSigner(
            userOpHash,
            userOp.signature
        );
        // Range will be checked by Entrypoint
        (uint48 validAfter, uint48 validUntil) = userOp.extractValidatorData();
        // Max quota per validator is ~18ETH (2**64/10**18) in gas fees
        bool validationFailed = !_checkAndUpdateQuota(validator, maxCost);
        validationData = _packValidationData(
            validationFailed,
            validUntil,
            validAfter
        );
    }

    // Slashing amount is based on quota and is converted back to tokens.
    function _adjustSlashingAmount(
        address validator,
        uint96 slashingAmount
    ) internal view override returns (uint96) {
        uint256 tokensToSlash = (uint256(slashingAmount) *
            10 ** QUOTA_PER_FEE_TOKEN_SCALE) / QUOTA_PER_FEE_TOKEN;
        uint96 tokenBalance = quotaStatsForSigner[validator].tokenBalance;
        // If more tokens than locked should be slashed, we slash what we can get
        return
            tokensToSlash > tokenBalance ? tokenBalance : uint96(tokensToSlash);
    }
}

src/SafeInternationalHarbour.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {SignerAlreadySignedTransaction} from "./interfaces/Errors.sol";
import {
    SafeTransaction,
    SignatureDataWithTxHashIndex
} from "./interfaces/Types.sol";
import {SignatureStored, NewTransaction} from "./interfaces/Events.sol";
import {CoreLib} from "./libs/CoreLib.sol";
import {ERC4337Mixin, ERC4337MixinConfig} from "./mixins/ERC4337Mixin.sol";
// Imported for natspec inheritance
// solhint-disable-next-line no-unused-import
import {IHarbourStore} from "./interfaces/HarbourStore.sol";

/**
 * @title SafeInternationalHarbour
 * @notice Permissionless, append-only registry that lets **any EOA signer** publish Safe
 *         transactions ("SafeTx") and their signatures. Clients without an off-chain indexer can reconstruct the full multisig payload with only:
 *         1. the Safe address;
 *         2. the target `chainId`;
 *         3. the Safe `nonce`; and
 *         4. the current Safe owners set.
 *
 * Each unique `safeTxHash` (EIP-712 digest of the SafeTx struct) is persisted **once** together with
 * its parameters. Signatures are appended under the composite key
 * `(signer, safe, chainId, nonce)`, enabling on-chain, gas-efficient look-ups.
 *
 * ### ⚠️ Contract-based signers unsupported
 * Only ECDSA signatures from externally-owned accounts (EOAs) are supported. Contract wallets that
 * rely on ERC-1271 or similar cannot be verified on-chain in a chain-agnostic way and are therefore
 * **not supported**.
 *
 * @dev The {SignatureStored} event is the only hook required by indexers; however, the contract is
 *      fully functional without any off-chain infrastructure.
 */
contract SafeInternationalHarbour is ERC4337Mixin {
    // ------------------------------------------------------------------
    // Storage
    // ------------------------------------------------------------------

    // Mapping `safeTxHash → SafeTransaction` parameters
    mapping(bytes32 => SafeTransaction) private _txDetails;

    // Mapping `signer → safe → chainId → nonce → SignatureData[]`
    // Stores the list of signatures provided by a signer for a given Safe context.
    // Note: A single list entry here could contain signatures for *different* `safeTxHash` values
    // if those transactions share the same (safe, chainId, nonce). Use `_hasSignerSignedTx`
    // to ensure a signer only signs a specific `safeTxHash` once.
    struct SignatureData {
        bytes32 r;
        bytes32 vs;
    }
    mapping(address signer => mapping(address safe => mapping(uint256 chainId => mapping(uint256 nonce => SignatureData[]))))
        private _sigData;

    mapping(bytes32 signatureHash => bytes32 safeTxHash) private _signatureLink;

    constructor(
        ERC4337MixinConfig memory _erc4337Mixinconfig
    ) ERC4337Mixin(_erc4337Mixinconfig) {}

    // ------------------------------------------------------------------
    // External & public write functions
    // ------------------------------------------------------------------

    /**
     * @notice Publish a Safe transaction and/or append a signature to it.
     *
     * @dev If `safeTxHash` has been seen before, its parameters are *not* validated nor overwritten –
     *      the call simply appends the `(r,s)` pair for `signer`.
     *
     * @param safeAddress    Target Safe Smart-Account.
     * @param chainId        Chain id the transaction is meant for.
     * @param nonce          Safe nonce.
     * @param to             Destination of the inner call/delegatecall.
     * @param value          ETH value forwarded by the Safe.
     * @param data           Calldata executed by the Safe.
     * @param operation      0 = CALL, 1 = DELEGATECALL.
     * @param safeTxGas      Gas forwarded to the inner call.
     * @param baseGas        Fixed overhead reimbursed to the submitting signer.
     * @param gasPrice       Gas price used for reimbursement.
     * @param gasToken       ERC-20 token address for refunds (`address(0)` = ETH).
     * @param refundReceiver Address receiving the gas refund.
     * @param signature      **Single** 65-byte ECDSA signature.
     *
     * @return listIndex     Index of the stored signature in the signer-specific list.
     *
     * @custom:events Emits {SignatureStored}.
     */
    function enqueueTransaction(
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        bytes calldata signature
    ) external returns (uint256 listIndex) {
        // ------------------------------------------------------------------
        // Build the EIP-712 digest that uniquely identifies the SafeTx
        // ------------------------------------------------------------------
        bytes32 safeTxHash = CoreLib.computeSafeTxHash(
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        (address signer, bytes32 r, bytes32 vs) = CoreLib.recoverSigner(
            safeTxHash,
            signature
        );

        _storeTransaction(
            safeTxHash,
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        // --- DUPLICATE TRANSACTION SIGNATURE CHECK ---
        // Revert if this signer has already submitted *any* signature for this *exact* safeTxHash
        require(
            !_signatureStored(keccak256(abi.encodePacked(r, vs))),
            SignerAlreadySignedTransaction(signer, safeTxHash)
        );
        return
            _storeSignature(
                signer,
                safeAddress,
                chainId,
                nonce,
                safeTxHash,
                r,
                vs
            );
    }

    // ------------------------------------------------------------------
    // External & public read functions
    // ------------------------------------------------------------------

    /**
     * @notice Retrieve the full parameter set of a Safe transaction.
     *
     * @param safeTxHash EIP-712 digest of the transaction.
     *
     * @return txParams Struct with all SafeTx parameters (zero-initialised if unknown).
     */
    function retrieveTransaction(
        bytes32 safeTxHash
    ) external view returns (SafeTransaction memory txParams) {
        txParams = _txDetails[safeTxHash];
    }

    /**
     * @notice Paginated getter for signature entries.
     *
     * @param signerAddress Address that created the signatures.
     * @param safeAddress   Safe Smart-Account.
     * @param chainId       Target chain id.
     * @param nonce         Safe nonce.
     * @param start         Zero-based start index of the slice.
     * @param count         Maximum number of entries to return.
     *
     * @return page       Array slice `[start … start+count)` (may be shorter).
     * @return totalCount Total number of signatures stored for the tuple.
     */
    function retrieveSignatures(
        address signerAddress,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        uint256 start,
        uint256 count
    )
        external
        view
        returns (SignatureDataWithTxHashIndex[] memory page, uint256 totalCount)
    {
        SignatureData[] storage all = _sigData[signerAddress][safeAddress][
            chainId
        ][nonce];
        totalCount = all.length;
        if (start >= totalCount)
            return (new SignatureDataWithTxHashIndex[](0), totalCount);

        uint256 end = start + count;
        if (end > totalCount) end = totalCount;
        uint256 len = end - start;

        page = new SignatureDataWithTxHashIndex[](len);
        for (uint256 i; i < len; ++i) {
            bytes32 r = all[start + i].r;
            bytes32 vs = all[start + i].vs;
            page[i] = SignatureDataWithTxHashIndex({
                r: r,
                vs: vs,
                txHash: _signatureLink[keccak256(abi.encodePacked(r, vs))]
            });
        }
    }

    /**
     * @notice Convenience getter returning the **number** of signatures stored for the key tuple.
     *
     * @param signerAddress Signer address.
     * @param safeAddress   Safe Smart-Account.
     * @param chainId       Target chain id.
     * @param nonce         Safe nonce.
     *
     * @return count Length of the signature list.
     */
    function retrieveSignaturesCount(
        address signerAddress,
        address safeAddress,
        uint256 chainId,
        uint256 nonce
    ) external view returns (uint256 count) {
        count = _sigData[signerAddress][safeAddress][chainId][nonce].length;
    }

    // ------------------------------------------------------------------
    // Internal functions
    // ------------------------------------------------------------------

    /**
     * @inheritdoc IHarbourStore
     */
    function _signatureStored(
        bytes32 signatureHash
    ) internal view override returns (bool signed) {
        signed = _signatureLink[signatureHash] != 0;
    }

    /**
     * @inheritdoc IHarbourStore
     */
    function _storeTransaction(
        bytes32 safeTxHash,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver
    ) internal override {
        // Store parameters only once (idempotent write)
        SafeTransaction storage slot = _txDetails[safeTxHash];
        if (!slot.stored) {
            // first encounter → persist full parameter set
            slot.stored = true;
            slot.to = to;
            slot.operation = operation;

            // Writing to storage is expensive, so we only write if the value is non-zero
            if (value > 0) {
                slot.value = CoreLib.safeCastUint256ToUint128(value);
            }
            if (safeTxGas > 0) {
                slot.safeTxGas = CoreLib.safeCastUint256ToUint128(safeTxGas);
            }
            if (baseGas > 0) {
                slot.baseGas = CoreLib.safeCastUint256ToUint128(baseGas);
            }
            if (gasPrice > 0) {
                slot.gasPrice = CoreLib.safeCastUint256ToUint128(gasPrice);
            }
            if (gasToken != address(0)) {
                slot.gasToken = gasToken;
            }
            if (refundReceiver != address(0)) {
                slot.refundReceiver = refundReceiver;
            }
            if (data.length > 0) {
                slot.data = data;
            }

            emit NewTransaction(
                safeTxHash,
                safeAddress,
                chainId,
                nonce,
                to,
                value,
                operation,
                safeTxGas,
                baseGas,
                gasPrice,
                gasToken,
                refundReceiver,
                data
            );
        }
    }

    /**
     * @inheritdoc IHarbourStore
     */
    function _storeSignature(
        address signer,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        bytes32 safeTxHash,
        bytes32 r,
        bytes32 vs
    ) internal override returns (uint256 listIndex) {
        _signatureLink[keccak256(abi.encodePacked(r, vs))] = safeTxHash;

        SignatureData[] storage list = _sigData[signer][safeAddress][chainId][
            nonce
        ];
        listIndex = list.length;

        list.push(SignatureData({r: r, vs: vs}));

        emit SignatureStored(
            signer,
            safeAddress,
            safeTxHash,
            chainId,
            nonce,
            listIndex
        );
    }
}

src/SafeSecretHarbour.sol.content:
// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    NothingToEnqueue,
    SignerAlreadySignedTransaction,
    UnexpectedSigner
} from "./interfaces/Errors.sol";
import {
    EncryptionKeyRegistered,
    SafeTransactionRegistered,
    SafeTransactionSigned
} from "./interfaces/Events.sol";
import {ISafeSecretHarbour} from "./interfaces/Harbour.sol";
import {
    EncryptionKey,
    SafeTransactionRegistrationHandle
} from "./interfaces/Types.sol";
import {IERC165} from "./interfaces/ERC165.sol";
import {BlockNumbers} from "./libs/BlockNumbers.sol";
import {CoreLib} from "./libs/CoreLib.sol";
import {RegistrationKey} from "./libs/RegistrationKey.sol";

/**
 * @title Safe Secret Harbour
 * @notice Permissionless, append-only registry that lets **any EOA signer** publish encrypted Safe
 *         transactions ("SafeTx") and their signatures. Clients without an off-chain indexer can
 *         reconstruct the full multisig payload with only:
 *         1. the Safe address;
 *         2. the target `chainId`;
 *         3. the Safe `nonce`;
 *         4. the current Safe owners set; and
 *         5. a key to decrypt the transaction payload.
 *
 *         Each unique `safeTxHash` (ERC-712 digest of the SafeTx struct) may be registered more
 *         than once, to allow rotating the encryption.
 *
 *         Additionally, this contract contains a public encryption key registry, and functions as
 *         a trustless channel for signers to communicate public encryption keys amongst themselves
 *         in order to support asymmetric encryption schemes of the transaction data.
 */
contract SafeSecretHarbour is IERC165, ISafeSecretHarbour {
    using BlockNumbers for BlockNumbers.T;
    using BlockNumbers for BlockNumbers.Iterator;
    using RegistrationKey for RegistrationKey.T;

    // ------------------------------------------------------------------
    // Storage
    // ------------------------------------------------------------------

    /**
     * @dev Mapping of signers to their encryption keys.
     */
    mapping(address signer => EncryptionKey) private _encryptionKeys;

    /**
     * @dev Mapping of registration key to an array of block numbers where a Safe transaction was
     *      registered with the harbour.
     *
     *      Note that the same Safe transaction can be registered **multiple times** with harbour.
     *      This is important to allow appending additional encryption data (such as new wrapped
     *      encryption keys for signers that either rotated or registered their public encryption
     *      key after the initial transaction submission.
     */
    mapping(RegistrationKey.T => BlockNumbers.T) private _registrations;

    /**
     * @dev Mapping per signer from Safe transaction hashes to the block in which a signature was
     *      registered.
     */
    mapping(address signer => mapping(bytes32 safeTxHash => uint256 blockNumber))
        private _signatures;

    // ------------------------------------------------------------------
    // ERC-165 Implementation
    // ------------------------------------------------------------------

    /// @inheritdoc IERC165
    function supportsInterface(
        bytes4 interfaceId
    ) external pure returns (bool) {
        return
            interfaceId == type(IERC165).interfaceId ||
            interfaceId == type(ISafeSecretHarbour).interfaceId;
    }

    // ------------------------------------------------------------------
    // External & public write functions
    // ------------------------------------------------------------------

    /**
     * @notice Register a public encryption key for a signer.
     *
     * @param context   A 32-byte context specific to the public encryption key.
     * @param publicKey The public encryption key to be registered for the `msg.sender` signer.
     */
    function registerEncryptionKey(
        bytes32 context,
        bytes32 publicKey
    ) external {
        _registerEncryptionKey(msg.sender, context, publicKey);
    }

    /**
     * @notice Register a public encryption key on behalf of a signer.
     *
     * @param signer    The signer to register the encryption key for.
     * @param context   A 32-byte context specific to the public encryption key.
     * @param publicKey The public encryption key to be registered for the `signer`.
     * @param signature The ECDSA signature of the `signer` over the encryption key registration.
     */
    function registerEncryptionKeyFor(
        address signer,
        bytes32 context,
        bytes32 publicKey,
        bytes calldata signature
    ) external {
        bytes32 encryptionKeyHash = CoreLib.computeEncryptionKeyHash(
            block.chainid,
            address(this),
            context,
            publicKey
        );
        (address recoveredSigner, , ) = CoreLib.recoverSigner(
            encryptionKeyHash,
            signature
        );
        require(recoveredSigner == signer, UnexpectedSigner(recoveredSigner));
        _registerEncryptionKey(signer, context, publicKey);
    }

    /**
     * @notice Register an encrypted Safe transaction and signature.
     *
     * @dev This function serves two purposes:
     *      1. To register Safe transaction encryption data
     *      2. To add signatures to a Safe transaction
     *
     * @param chainId          Chain id the transaction is meant for.
     * @param safe             Target Safe Smart-Account.
     * @param nonce            Safe nonce.
     * @param safeTxStructHash The ERC-712 struct hash of the Safe transaction data.
     * @param encryptionBlob   A blob containing encrypted transaction data. This can either be the
     *                         encrypted transaction itself, or additional encrypted keys to new
     *                         signer public keys. The exact format is not enforced and application
     *                         dependent. There are no guarantees that the blob actually matches the
     *                         provided `safeTxStructHash`. The reference implementation uses JWE to
     *                         encrypt the RLP-encoded Safe transaction with key wrapping for the
     *                         recipients' X25519 public keys. _Optional_: can be omitted to
     *                         register a signature without any new encrypted transaction data.
     * @param signature        A 65-byte ECDSA signature. _Optional_: can be omitted to register
     *                         new encrypted transaction data without a signature.
     *
     * @return uid             The unique signer-specific identifer of the transaction registration.
     *                         Zero if `encryptionBlob` is empty.
     *
     * @custom:events Emits {SafeTransactionSigned} and {SafeTransactionRegistered}.
     */
    function enqueueTransaction(
        uint256 chainId,
        address safe,
        uint256 nonce,
        bytes32 safeTxStructHash,
        bytes calldata signature,
        bytes calldata encryptionBlob
    ) external returns (bytes32 uid) {
        require(
            signature.length | encryptionBlob.length != 0,
            NothingToEnqueue()
        );

        bytes32 safeTxHash = CoreLib.computePartialSafeTxHash(
            chainId,
            safe,
            safeTxStructHash
        );

        if (signature.length != 0) {
            _registerSignature(safeTxHash, signature);
        }

        if (encryptionBlob.length != 0) {
            uid = _registerTransaction(
                chainId,
                safe,
                nonce,
                msg.sender,
                safeTxHash,
                encryptionBlob
            );
        }
    }

    // ------------------------------------------------------------------
    // External & public read functions
    // ------------------------------------------------------------------

    /**
     * @notice Retrieves encryption public keys for the specified signers.
     *
     * @param signers     The list of signers to fetch encryption keys for.
     *
     * @return publicKeys The encryption public keys for each of the signers, or `0` if none was
     *                    registered. The keys are in the same order as the signers array (i.e. for
     *                    all `i`, the key for `signer[i]` is `publicKey[i]`).
     */
    function retrieveEncryptionPublicKeys(
        address[] calldata signers
    ) external view returns (bytes32[] memory publicKeys) {
        publicKeys = new bytes32[](signers.length);
        for (uint256 i = 0; i < signers.length; i++) {
            publicKeys[i] = _encryptionKeys[signers[i]].publicKey;
        }
    }

    /**
     * @notice Retrieves the encryption keys for a signer.
     *
     * @param signer         The signer to fetch encryption keys for.
     *
     * @return encryptionKey The registered encryption key.
     */
    function retrieveEncryptionKey(
        address signer
    ) external view returns (EncryptionKey memory encryptionKey) {
        encryptionKey = _encryptionKeys[signer];
    }

    /**
     * @notice Paginated getter for Safe transaction registrations.
     *
     * @dev Note that this function does not return the full registered transaction directly, but
     *      a unique identifier and block number that can be used to query an log with the full
     *      transaction details.
     *
     * @param chainId     Target chain id.
     * @param safe        Safe Smart-Account.
     * @param nonce       Safe nonce.
     * @param notary      Address that registered ecrypted transaction data.
     * @param start       Zero-based start index of the slice.
     * @param count       Maximum number of entries to return.
     *
     * @return page       Array slice `[start … start+count)` (may be shorter).
     * @return totalCount Total number of registrations for the `(chainId, safe, nonce, signer)`
     *                    tuple.
     */
    function retrieveRegistrations(
        uint256 chainId,
        address safe,
        uint256 nonce,
        address notary,
        uint256 start,
        uint256 count
    )
        external
        view
        returns (
            SafeTransactionRegistrationHandle[] memory page,
            uint256 totalCount
        )
    {
        RegistrationKey.T registration = RegistrationKey.get(
            chainId,
            safe,
            nonce,
            notary
        );
        BlockNumbers.Iterator memory it = _registrations[registration].iter();

        totalCount = it.count();
        it.skip(start);
        it.take(count);

        page = new SafeTransactionRegistrationHandle[](it.count());
        unchecked {
            for (uint256 i = 0; it.next(); i++) {
                SafeTransactionRegistrationHandle memory item = page[i];
                item.blockNumber = it.value();
                item.uid = registration.uniqueIdentifier(start + i);
            }
        }
    }

    /**
     * @notice Convenience getter returning the **number** of registrations stored for a specific
     *         `(chainId, safe, nonce, notary)` tuple.
     *
     * @param chainId Target chain id.
     * @param safe    Safe Smart-Account.
     * @param nonce   Safe nonce.
     * @param notary  Notary address.
     *
     * @return count  Number of registrations.
     */
    function retrieveRegistrationCount(
        uint256 chainId,
        address safe,
        uint256 nonce,
        address notary
    ) external view returns (uint256 count) {
        RegistrationKey.T registration = RegistrationKey.get(
            chainId,
            safe,
            nonce,
            notary
        );
        count = _registrations[registration].len();
    }

    /**
     * @notice Retrieve Safe transaction signatures for the specified signers.
     *
     * @dev Note that this function does not return the signature directly, the block number that
     *      can be used to query an log with the signature bytes.
     *
     * @param signers    The signer addresses.
     * @param safeTxHash ERC-712 digest of the transaction
     *
     * @return blockNumbers The block numbers containing the Safe transaction signature events for
     *                      the specified signers and Safe transaction hash.
     */
    function retrieveSignatures(
        address[] calldata signers,
        bytes32 safeTxHash
    ) external view returns (uint256[] memory blockNumbers) {
        blockNumbers = new uint256[](signers.length);
        unchecked {
            for (uint256 i = 0; i < signers.length; i++) {
                blockNumbers[i] = _signatures[signers[i]][safeTxHash];
            }
        }
    }

    // ------------------------------------------------------------------
    // Private functions
    // ------------------------------------------------------------------

    /**
     * @notice Private function to register a public encryption key for a signer.
     *
     * @param signer    The signer to register the encryption key for.
     * @param context   A 32-byte context specific to the public encryption key.
     * @param publicKey The public encryption key to be registered for the `signer`.
     */
    function _registerEncryptionKey(
        address signer,
        bytes32 context,
        bytes32 publicKey
    ) private {
        _encryptionKeys[signer] = EncryptionKey(context, publicKey);
        emit EncryptionKeyRegistered(signer, context, publicKey);
    }

    /**
     * @dev Private function to register a Safe transaction.
     *
     * @param chainId        Chain id the transaction is meant for.
     * @param safe           Safe Smart-Account.
     * @param nonce          Safe nonce.
     * @param notary         The account that submitted the encrypted transaction data blob for
     *                       registration.
     * @param safeTxHash     ERC-712 digest of the transaction.
     * @param encryptionBlob The Safe transaction encryption blob.
     *
     * @return uid       The unique signer-specific identifer of the registration.
     */
    function _registerTransaction(
        uint256 chainId,
        address safe,
        uint256 nonce,
        address notary,
        bytes32 safeTxHash,
        bytes calldata encryptionBlob
    ) private returns (bytes32 uid) {
        RegistrationKey.T registration = RegistrationKey.get(
            chainId,
            safe,
            nonce,
            notary
        );
        uint256 index = _registrations[registration].append(block.number);

        uid = registration.uniqueIdentifier(index);
        emit SafeTransactionRegistered(uid, safeTxHash, encryptionBlob);
    }

    /**
     * @dev Private function to register a Safe transaction signature after validation.
     *
     * @param safeTxHash ERC-712 digest of the transaction.
     * @param signature  The ECDSA signature bytes.
     */
    function _registerSignature(
        bytes32 safeTxHash,
        bytes calldata signature
    ) private {
        (address signer, , ) = CoreLib.recoverSigner(safeTxHash, signature);
        require(
            _signatures[signer][safeTxHash] == 0,
            SignerAlreadySignedTransaction(signer, safeTxHash)
        );

        _signatures[signer][safeTxHash] = block.number;
        emit SafeTransactionSigned(signer, safeTxHash, signature);
    }
}

