// // SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

// ------------------------------------------------------------------
// Data structures
// ------------------------------------------------------------------

/**
 * @dev Storage optimised mirror of the SafeTx struct used by Safe contracts.
 *      Non-optimised version uses uint256 for:
 *      - value
 *      - safeTxGas
 *      - baseGas
 *      - gasPrice
 */
struct SafeTransaction {
    // stored, operation and to will be packed into the same storage slot
    bool stored;
    uint8 operation;
    address to;
    uint128 value;
    uint128 safeTxGas;
    uint128 baseGas;
    uint128 gasPrice;
    address gasToken;
    address refundReceiver;
    bytes data;
}

/**
 * @dev Minimal, storage‑optimised representation of an ECDSA signature.
 */
struct SignatureDataWithTxHashIndex {
    bytes32 r;
    // vs is the compact representation of s and v coming from
    // EIP-2098: https://eips.ethereum.org/EIPS/eip-2098
    bytes32 vs;
    bytes32 txHash; // EIP‑712 digest this signature belongs to
}

src/libs/CoreLib.sol.content:
// // SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import "../interfaces/Constants.sol";
import "../interfaces/Types.sol";
import "../interfaces/Errors.sol";
import "../interfaces/Events.sol";

library CoreLib {
    // ------------------------------------------------------------------
    // Internal functions
    // ------------------------------------------------------------------

    /**
     * @notice Computes the unique EIP-712 digest for a SafeTx using the provided parameters and domain.
     * @param safeAddress Address of the target Safe Smart Account.
     * @param chainId Chain ID included in the domain separator.
     * @param nonce Safe transaction nonce.
     * @param to Target address the Safe will call.
     * @param value ETH value to be sent with the call.
     * @param data Call data executed by the Safe.
     * @param operation Operation type: 0 = CALL, 1 = DELEGATECALL.
     * @param safeTxGas Gas limit for the Safe's internal execution.
     * @param baseGas Base gas overhead for reimbursement.
     * @param gasPrice Gas price used for reimbursement calculation.
     * @param gasToken Token address for refunds (0x0 for ETH).
     * @param refundReceiver Address to receive gas refunds.
     * @return safeTxHash Keccak256 digest of the EIP-712 encoded SafeTx.
     */
    function computeSafeTxHash(
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes memory data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver
    ) internal pure returns (bytes32 safeTxHash) {
        bytes32 domainSeparator = keccak256(
            abi.encode(DOMAIN_TYPEHASH, chainId, safeAddress)
        );
        bytes32 structHash = keccak256(
            abi.encode(
                SAFE_TX_TYPEHASH,
                to,
                value,
                keccak256(data),
                operation,
                safeTxGas,
                baseGas,
                gasPrice,
                gasToken,
                refundReceiver,
                nonce
            )
        );
        safeTxHash = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
    }

    /**
     * @notice Splits a 65-byte ECDSA signature into its components and recovers the signer address.
     * @param digest The message or data hash to verify (EIP-712 digest or eth_sign prefixed).
     * @param sig Concatenated 65-byte ECDSA signature (r || s || v).
     * @return signer The address that produced the signature (EOA).
     * @return r First 32 bytes of the ECDSA signature.
     * @return vs Compact representation of s and v coming from EIP-2098.
     * @dev Supports both EIP-712 and eth_sign flows by detecting v > 30 and applying the Ethereum Signed Message prefix.
     */
    function recoverSigner(
        bytes32 digest,
        bytes calldata sig
    ) internal pure returns (address signer, bytes32 r, bytes32 vs) {
        uint8 v;
        bytes32 s;
        // solhint-disable-next-line no-inline-assembly
        assembly ("memory-safe") {
            r := calldataload(sig.offset)
            s := calldataload(add(sig.offset, 0x20))
            v := byte(0, calldataload(add(sig.offset, 0x40)))
        }
        require(s <= SECP256K1_LOW_S_BOUND, InvalidSignatureSValue());

        signer = ecrecover(digest, v, r, s);
        require(signer != address(0), InvalidSignature());
        // solhint-disable-next-line no-inline-assembly
        assembly ("memory-safe") {
            // Equivalent to:
            // vs = bytes32(uint256(v - 27)  << 255 | uint256(s));
            // Which should avoid conversion between uint256 and bytes32
            vs := or(shl(255, sub(v, 27)), s)
        }
    }

    function safeCastUint256ToUint128(
        uint256 value
    ) internal pure returns (uint128) {
        require(value <= type(uint128).max, ValueDoesNotFitInUint128());
        return uint128(value);
    }
}

src/mixins/ERC4337Mixin.sol.content:
// // SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    HandlerContext
} from "@safe-global/safe-contracts/contracts/handler/HandlerContext.sol";
import {IAccount} from "@account-abstraction/contracts/interfaces/IAccount.sol";
import {
    IEntryPoint
} from "@account-abstraction/contracts/interfaces/IEntryPoint.sol";
import {
    PackedUserOperation
} from "@account-abstraction/contracts/interfaces/PackedUserOperation.sol";
import {
    _packValidationData
} from "@account-abstraction/contracts/core/Helpers.sol";
import {
    UserOperationLib
} from "@account-abstraction/contracts/core/UserOperationLib.sol";
import "../interfaces/Errors.sol";
import "../interfaces/HarbourStore.sol";
import "../interfaces/QuotaManager.sol";
import "../libs/CoreLib.sol";

abstract contract ERC4337Mixin is IAccount, IHarbourStore, IQuotaManager {
    using UserOperationLib for PackedUserOperation;

    struct ERC4337MixinConfig {
        address entryPoint;
        uint256 maxPriorityFee;
        uint256 preVerificationGasPerByte;
        uint256 preVerificationBaseGas;
        uint256 verificationGasPerByte;
        uint256 callGasPerByte;
    }

    // ------------------------------------------------------------------
    // 4337 functions
    // ------------------------------------------------------------------

    /**
     * @notice The address of the EntryPoint contract supported by this module.
     */
    address public immutable SUPPORTED_ENTRYPOINT;
    // TODO evaluate if this should be upgradable
    uint256 public immutable MAX_PRIORITY_FEE;
    uint256 public immutable PRE_VERIFICATION_GAS_PER_BYTE;
    uint256 public immutable PRE_VERIFICATION_BASE_GAS;
    uint256 public immutable VERIFICATION_GAS_PER_BYTE;
    uint256 public immutable CALL_GAS_PER_BYTE;

    constructor(ERC4337MixinConfig memory _config) {
        SUPPORTED_ENTRYPOINT = _config.entryPoint;
        MAX_PRIORITY_FEE = _config.maxPriorityFee;
        PRE_VERIFICATION_GAS_PER_BYTE = _config.preVerificationGasPerByte;
        PRE_VERIFICATION_BASE_GAS = _config.preVerificationBaseGas;
        VERIFICATION_GAS_PER_BYTE = _config.verificationGasPerByte;
        CALL_GAS_PER_BYTE = _config.callGasPerByte;
    }

    /**
     * Return the account nonce.
     * This method returns the next sequential nonce.
     * For a nonce of a specific key, use `entrypoint.getNonce(account, key)`
     */
    function getNonce(address signer) public view virtual returns (uint256) {
        return
            IEntryPoint(SUPPORTED_ENTRYPOINT).getNonce(
                address(this),
                uint192(uint160(signer))
            );
    }

    function validateUserOp(
        PackedUserOperation calldata userOp,
        bytes32,
        uint256
    ) external override returns (uint256 validationData) {
        // Assumption:
        //   - UserOp signature is SafeTx signature
        // Requirements:
        //   - [x] Check entrypoint
        //   - [x] No paymaster support
        // Steps:
        //   - [x] decode callData
        //   - [x] recover signer from SafeTx signature
        //   - [x] check that signer has not submitted signature
        //   - [x] check that nonce key is signer address
        //   - [x] check limits on fee params
        //   - [ ] usage checks

        require(
            msg.sender == SUPPORTED_ENTRYPOINT,
            InvalidEntryPoint(msg.sender)
        );
        require(
            userOp.paymasterAndData.length == 0,
            InvalidUserOpPaymasterAndData()
        );
        require(userOp.signature.length == 65, InvalidECDSASignatureLength());

        require(
            bytes4(userOp.callData) == this.storeTransaction.selector,
            InvalidTarget(bytes4(userOp.callData))
        );
        (
            bytes32 safeTxHash,
            address signer,
            bytes32 r,
            bytes32 vs,
            uint256 computedDataLength
        ) = _verifySafeTxData(userOp.callData[4:]);

        // --- DUPLICATE TRANSACTION SIGNATURE CHECK ---
        // Revert if this signer has already submitted *any* signature for this *exact* safeTxHash
        require(
            !_signerSignedTx(safeTxHash, signer),
            SignerAlreadySignedTransaction(signer, safeTxHash)
        );

        _verifySignature(safeTxHash, userOp.signature, signer, r, vs);

        uint256 nonce = getNonce(signer);
        require(userOp.nonce == nonce, UnexpectedNonce(nonce));

        // We skip the check that missingAccountFunds should be == 0, as this is the job of the entry point

        // `computedDataLength` is used for validations, as userOp.callData can be extended to manipulate the fees
        bool validationFailed = !_validGasFees(userOp) ||
            !_validGasLimits(userOp, computedDataLength) ||
            !_checkAndUpdateQuota(signer, computedDataLength);
        return _packValidationData(validationFailed, 0, 0);
    }

    function _validGasFees(
        PackedUserOperation calldata userOp
    ) private view returns (bool) {
        uint256 maxPriorityFeePerGas = userOp.unpackMaxPriorityFeePerGas();
        return maxPriorityFeePerGas <= MAX_PRIORITY_FEE;
    }

    function _validGasLimits(
        PackedUserOperation calldata userOp,
        uint256 computedDataLength
    ) private view returns (bool) {
        // Base calculations of gas limits on calldata size, this is a simple workaround for now
        //      -> an alernative for verificationGas could be to do internal gas metering
        // Employ a maximum gas limit based on locked tokens
        if (
            userOp.preVerificationGas >
            computedDataLength *
                PRE_VERIFICATION_GAS_PER_BYTE +
                PRE_VERIFICATION_BASE_GAS
        ) return false;
        uint256 verificationGasLimit = userOp.unpackVerificationGasLimit();
        if (
            verificationGasLimit >
            computedDataLength * VERIFICATION_GAS_PER_BYTE
        ) return false;
        uint256 callGasLimit = userOp.unpackCallGasLimit();
        if (callGasLimit > computedDataLength * CALL_GAS_PER_BYTE) return false;
        return true;
    }

    function _verifySafeTxData(
        bytes calldata callData
    ) private pure returns (bytes32, address, bytes32, bytes32, uint256) {
        (
            bytes32 safeTxHash,
            address safeAddress,
            uint256 chainId,
            uint256 nonce,
            address to,
            uint256 value,
            bytes memory data,
            uint8 operation,
            uint256 safeTxGas,
            uint256 baseGas,
            uint256 gasPrice,
            address gasToken,
            address refundReceiver,
            address signer,
            bytes32 r,
            bytes32 vs
        ) = abi.decode(
                callData,
                (
                    bytes32,
                    address,
                    uint256,
                    uint256,
                    address,
                    uint256,
                    bytes,
                    uint8,
                    uint256,
                    uint256,
                    uint256,
                    address,
                    address,
                    address,
                    bytes32,
                    bytes32
                )
            );
        bytes32 computedSafeTxHash = CoreLib.computeSafeTxHash(
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        require(
            computedSafeTxHash == safeTxHash,
            UnexpectedSafeTxHash(computedSafeTxHash)
        );
        // The computed length when properly encoded is based on the data length and the number of params
        // 4 bytes selector + 15 params each 32 bytes + 32 bytes offset of data + 32 bytes length of data + data length
        return (safeTxHash, signer, r, vs, data.length + 17 * 32 + 4);
    }

    function _verifySignature(
        bytes32 safeTxHash,
        bytes calldata signature,
        address signer,
        bytes32 r,
        bytes32 vs
    ) private pure {
        (
            address recoveredSigner,
            bytes32 extractedR,
            bytes32 extractedVS
        ) = CoreLib.recoverSigner(safeTxHash, signature);
        require(signer == recoveredSigner, UnexpectedSigner(signer));
        require(r == extractedR, UnexpectedSignatureR(r));
        require(vs == extractedVS, UnexpectedSignatureVS(vs));
    }

    function storeTransaction(
        bytes32 safeTxHash,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        address signer,
        bytes32 r,
        bytes32 vs
    ) external returns (uint256 listIndex) {
        require(
            msg.sender == SUPPORTED_ENTRYPOINT,
            InvalidEntryPoint(msg.sender)
        );
        _storeTransaction(
            safeTxHash,
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );
        return
            _storeSignature(
                signer,
                safeAddress,
                chainId,
                nonce,
                safeTxHash,
                r,
                vs
            );
    }
}

src/mixins/QuotaMixin.sol.content:
// // SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interfaces/QuotaManager.sol";
import "../libs/CoreLib.sol";

struct QuotaStats {
    uint128 tokenBalance; // uint96 might also be enough, as this would be more tokens than most project have in circulation
    uint64 usedQuota; // Quota is limited to 64 bits (current Quota == Bytes of data, so that should be ok)
    uint64 nextQuotaReset; // timestamps are Safe to limit to 64 bits
}

abstract contract QuotaMixin is IQuotaManager {
    using SafeERC20 for IERC20;

    event Withdraw(address indexed signer, uint256 indexed amount);
    event Deposit(address indexed signer, uint256 indexed amount);

    struct QuotaMixinConfig {
        uint64 timeframeQuotaReset;
        uint16 requiredQuotaMultiplier;
        uint32 freeQuotaPerDepositedFeeToken;
        uint32 maxFreeQuota;
        address feeToken;
        uint8 feeTokenDecimals;
    }

    mapping(address => QuotaStats) public quotaStatsForSigner;
    mapping(address => mapping(bytes32 => uint256)) public withdrawsForSigner;

    uint64 public immutable TIMEFRAME_QUOTA_RESET;
    uint32 public immutable FREE_QUOTA_PER_DEPOSITED_FEE_TOKEN;
    uint16 public immutable REQUIRED_QUOTA_MULTIPLIER;
    uint32 public immutable MAX_FREE_QUOTA;
    address public immutable FEE_TOKEN;
    uint8 public immutable FEE_TOKEN_DECIMALS;

    constructor(QuotaMixinConfig memory _config) {
        TIMEFRAME_QUOTA_RESET = _config.timeframeQuotaReset;
        FREE_QUOTA_PER_DEPOSITED_FEE_TOKEN = _config
            .freeQuotaPerDepositedFeeToken;
        REQUIRED_QUOTA_MULTIPLIER = _config.requiredQuotaMultiplier;
        MAX_FREE_QUOTA = _config.maxFreeQuota;
        FEE_TOKEN = _config.feeToken;
        FEE_TOKEN_DECIMALS = _config.feeTokenDecimals;
    }

    function depositTokensForSigner(
        address signer,
        uint128 amount
    ) public payable {
        // We don't update the nextQuotaReset this way depositing more tokens does not negatively affect the reset schedule
        // The reset schedule always starts from 0, therefore is always a multiple of the reset timeframe (unless the timeframe is changed)
        quotaStatsForSigner[signer].tokenBalance += amount;
        IERC20(FEE_TOKEN).safeTransferFrom(msg.sender, address(this), amount);
        emit Deposit(signer, amount);
    }

    function computeWithdrawHash(
        uint256 amount,
        address beneficiary,
        uint256 nonce
    ) internal view returns (bytes32 withdrawHash) {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(uint256 chainId,address verifyingContract)"
                ),
                block.chainid,
                address(this)
            )
        );
        bytes32 structHash = keccak256(
            abi.encode(
                keccak256(
                    "WithdrawRequest(uint256 amount,address beneficiary,uint256 nonce)"
                ),
                amount,
                beneficiary,
                nonce
            )
        );
        withdrawHash = keccak256(
            abi.encodePacked("\x19\x01", domainSeparator, structHash)
        );
    }

    function widthdrawTokensForSigner(
        bytes calldata signature,
        uint128 amount,
        address beneficiary,
        uint256 nonce
    ) public payable {
        bytes32 withdrawHash = computeWithdrawHash(amount, beneficiary, nonce);
        (address signer, bytes32 r, bytes32 vs) = CoreLib.recoverSigner(
            withdrawHash,
            signature
        );
        // Silence unused local variable warning
        (r, vs);
        // Check that withdrawal was not executed yet
        require(withdrawsForSigner[signer][withdrawHash] == 0, "Withdrawal was already performed");
        withdrawsForSigner[signer][withdrawHash] = block.timestamp;

        QuotaStats storage stats = quotaStatsForSigner[signer];
        require(stats.tokenBalance >= amount, "Insufficient Tokens");
        // We use the quota reset timeframe as a unlock timeframe
        // -> currently the signer is not allowed to sign any Safe transaction during this timeframe
        // TODO: have dedicated unlock logic (also to avoid some fee exploit flows)
        require(
            stats.nextQuotaReset < block.timestamp,
            "Tokens have been used during this timeframe"
        );

        stats.tokenBalance -= amount;

        IERC20(FEE_TOKEN).safeTransfer(beneficiary, amount);
        emit Withdraw(signer, amount);
    }

    function availableFreeQuotaForSigner(
        address signer
    )
        public
        view
        returns (
            uint64 availableFreeQuota,
            uint64 usedSignerQuota,
            uint64 nextSignerQuotaReset
        )
    {
        QuotaStats memory stats = quotaStatsForSigner[signer];
        nextSignerQuotaReset = stats.nextQuotaReset;
        if (nextSignerQuotaReset > block.timestamp) {
            usedSignerQuota = stats.usedQuota;
        } else {
            uint64 blocktime = uint64(block.timestamp);
            nextSignerQuotaReset = blocktime -
                ((blocktime - nextSignerQuotaReset) % TIMEFRAME_QUOTA_RESET) +
                TIMEFRAME_QUOTA_RESET;
        }
        // We cast tokenBalance to uint256 to use more bits for the arithmetics
        uint256 maxSignerQuota = (uint256(stats.tokenBalance) *
            FREE_QUOTA_PER_DEPOSITED_FEE_TOKEN) / 10 ** FEE_TOKEN_DECIMALS;

        require(
            maxSignerQuota <= type(uint64).max,
            "Max signer quota too high"
        );

        uint64 freeSignerQuota = uint64(maxSignerQuota);
        if (freeSignerQuota > MAX_FREE_QUOTA) {
            freeSignerQuota = MAX_FREE_QUOTA;
        }
        if (usedSignerQuota <= freeSignerQuota) {
            availableFreeQuota = freeSignerQuota - usedSignerQuota;
        } else {
            availableFreeQuota = 0;
        }
    }

    function _updateQuotaParams(
        address signer,
        uint64 newSignerQuota,
        uint64 nextSignerQuotaReset
    ) internal {
        QuotaStats storage stats = quotaStatsForSigner[signer];
        if (nextSignerQuotaReset != stats.nextQuotaReset) {
            stats.nextQuotaReset = nextSignerQuotaReset;
        }
        stats.usedQuota = newSignerQuota;
    }

    function _checkAndUpdateQuota(
        address signer,
        uint256 requiredSignerQuota
    ) internal override returns (bool) {
        if (REQUIRED_QUOTA_MULTIPLIER == 0) return true;
        (
            uint64 availableFreeSignerQuota,
            uint64 usedSignerQuota,
            uint64 nextSignerQuotaReset
        ) = availableFreeQuotaForSigner(signer);
        uint256 adjustedRequiredSignerQuota = REQUIRED_QUOTA_MULTIPLIER *
            requiredSignerQuota;
        if (adjustedRequiredSignerQuota > availableFreeSignerQuota)
            return false;
        // Casting to uint64 is safe, as availableFreeSignerQuota is at most a uint64 and we compare it against that
        _updateQuotaParams(
            signer,
            usedSignerQuota + uint64(adjustedRequiredSignerQuota),
            nextSignerQuotaReset
        );
        return true;
    }
}

src/SafeInternationalHarbour.sol.content:
// // SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.29;

import "./interfaces/Constants.sol";
import "./interfaces/Errors.sol";
import "./interfaces/Types.sol";
import "./interfaces/Events.sol";
import "./libs/CoreLib.sol";
import "./mixins/ERC4337Mixin.sol";
import "./mixins/QuotaMixin.sol";

/**
 * @title SafeInternationalHarbour
 * @notice Permissionless, append‑only registry that lets **any EOA signer** publish Safe
 *         transactions ("SafeTx") and their signatures. Clients without an off‑chain indexer can reconstruct the full multisig payload with only:
 *         1. the Safe address;
 *         2. the target `chainId`;
 *         3. the Safe `nonce`; and
 *         4. the current Safe owners set.
 *
 * Each unique `safeTxHash` (EIP‑712 digest of the SafeTx struct) is persisted **once** together with
 * its parameters. Signatures are appended under the composite key
 * `(signer, safe, chainId, nonce)`, enabling on‑chain, gas‑efficient look‑ups.
 *
 * ### ⚠️ Contract‑based signers unsupported
 * Only ECDSA signatures from externally‑owned accounts (EOAs) are supported. Contract wallets that
 * rely on ERC‑1271 or similar cannot be verified on‑chain in a chain‑agnostic way and are therefore
 * **not supported**.
 *
 * @dev The {SignatureStored} event is the only hook required by indexers; however, the contract is
 *      fully functional without any off‑chain infrastructure.
 */
contract SafeInternationalHarbour is ERC4337Mixin, QuotaMixin {
    // ------------------------------------------------------------------
    // Storage
    // ------------------------------------------------------------------

    /// Mapping `safeTxHash → SafeTransaction` parameters
    mapping(bytes32 => SafeTransaction) private _txDetails;

    /// Mapping `signer → safe → chainId → nonce → SignatureDataWithTxHashIndex[]`
    /// Stores the list of signatures provided by a signer for a given Safe context.
    /// Note: A single list entry here could contain signatures for *different* `safeTxHash` values
    /// if those transactions share the same (safe, chainId, nonce). Use `_hasSignerSignedTx`
    /// to ensure a signer only signs a specific `safeTxHash` once.
    mapping(address signer => mapping(address safe => mapping(uint256 chainId => mapping(uint256 nonce => SignatureDataWithTxHashIndex[]))))
        private _sigData;

    /// @dev Tracks if a signer has already submitted *any* signature for a specific safeTxHash,
    ///      preventing duplicate signatures for the *exact same* transaction digest.
    ///      This complements `_sigData` by ensuring uniqueness per (safeTxHash, signer) pair.
    /// Mapping `safeTxHash → signer → bool`
    mapping(bytes32 safeTxHash => mapping(address signer => bool))
        private _hasSignerSignedTx;

    constructor(
        ERC4337MixinConfig memory _erc4337Mixinconfig,
        QuotaMixinConfig memory _quotaMixinconfig
    ) ERC4337Mixin(_erc4337Mixinconfig) QuotaMixin(_quotaMixinconfig) {}

    // ------------------------------------------------------------------
    // External & public write functions
    // ------------------------------------------------------------------

    /**
     * @notice Publish a Safe transaction and/or append a signature to it.
     *
     * @dev If `safeTxHash` has been seen before, its parameters are *not* validated nor overwritten –
     *      the call simply appends the `(r,s)` pair for `signer`.
     *
     * @param safeAddress    Target Safe Smart‑Account.
     * @param chainId        Chain id the transaction is meant for.
     * @param nonce          Safe nonce.
     * @param to             Destination of the inner call/delegatecall.
     * @param value          ETH value forwarded by the Safe.
     * @param data           Calldata executed by the Safe.
     * @param operation      0 = CALL, 1 = DELEGATECALL.
     * @param safeTxGas      Gas forwarded to the inner call.
     * @param baseGas        Fixed overhead reimbursed to the submitting signer.
     * @param gasPrice       Gas price used for reimbursement.
     * @param gasToken       ERC‑20 token address for refunds (`address(0)` = ETH).
     * @param refundReceiver Address receiving the gas refund.
     * @param signature      **Single** 65‑byte ECDSA signature.
     *
     * @return listIndex     Index of the stored signature in the signer‑specific list.
     *
     * @custom:events Emits {SignatureStored}.
     */
    function enqueueTransaction(
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver,
        bytes calldata signature
    ) external returns (uint256 listIndex) {
        require(signature.length == 65, InvalidECDSASignatureLength());

        // ------------------------------------------------------------------
        // Build the EIP‑712 digest that uniquely identifies the SafeTx
        // ------------------------------------------------------------------
        bytes32 safeTxHash = CoreLib.computeSafeTxHash(
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        (address signer, bytes32 r, bytes32 vs) = CoreLib.recoverSigner(
            safeTxHash,
            signature
        );

        _storeTransaction(
            safeTxHash,
            safeAddress,
            chainId,
            nonce,
            to,
            value,
            data,
            operation,
            safeTxGas,
            baseGas,
            gasPrice,
            gasToken,
            refundReceiver
        );

        // --- DUPLICATE TRANSACTION SIGNATURE CHECK ---
        // Revert if this signer has already submitted *any* signature for this *exact* safeTxHash
        require(
            !_signerSignedTx(safeTxHash, signer),
            SignerAlreadySignedTransaction(signer, safeTxHash)
        );
        return
            _storeSignature(
                signer,
                safeAddress,
                chainId,
                nonce,
                safeTxHash,
                r,
                vs
            );
    }

    // ------------------------------------------------------------------
    // External & public read functions
    // ------------------------------------------------------------------

    /**
     * @notice Retrieve the full parameter set of a Safe transaction.
     *
     * @param safeTxHash EIP‑712 digest of the transaction.
     *
     * @return txParams Struct with all SafeTx parameters (zero‑initialised if unknown).
     */
    function retrieveTransaction(
        bytes32 safeTxHash
    ) external view returns (SafeTransaction memory txParams) {
        txParams = _txDetails[safeTxHash];
    }

    /**
     * @notice Paginated getter for signature entries.
     *
     * @param signerAddress Address that created the signatures.
     * @param safeAddress   Safe Smart‑Account.
     * @param chainId       Target chain id.
     * @param nonce         Safe nonce.
     * @param start         Zero‑based start index of the slice.
     * @param count         Maximum number of entries to return.
     *
     * @return page       Array slice `[start … start+count)` (may be shorter).
     * @return totalCount Total number of signatures stored for the tuple.
     */
    function retrieveSignatures(
        address signerAddress,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        uint256 start,
        uint256 count
    )
        external
        view
        returns (SignatureDataWithTxHashIndex[] memory page, uint256 totalCount)
    {
        SignatureDataWithTxHashIndex[] storage all = _sigData[signerAddress][
            safeAddress
        ][chainId][nonce];
        totalCount = all.length;
        if (start >= totalCount)
            return (new SignatureDataWithTxHashIndex[](0), totalCount);

        uint256 end = start + count;
        if (end > totalCount) end = totalCount;
        uint256 len = end - start;

        page = new SignatureDataWithTxHashIndex[](len);
        for (uint256 i; i < len; ++i) {
            page[i] = all[start + i];
        }
    }

    /**
     * @notice Convenience getter returning the **number** of signatures stored for the key tuple.
     *
     * @param signerAddress Signer address.
     * @param safeAddress   Safe Smart‑Account.
     * @param chainId       Target chain id.
     * @param nonce         Safe nonce.
     *
     * @return count Length of the signature list.
     */
    function retrieveSignaturesCount(
        address signerAddress,
        address safeAddress,
        uint256 chainId,
        uint256 nonce
    ) external view returns (uint256 count) {
        count = _sigData[signerAddress][safeAddress][chainId][nonce].length;
    }

    // ------------------------------------------------------------------
    // Internal functions
    // ------------------------------------------------------------------

    /**
     * @dev Internal function to store the transaction data and signature after validation.
     *
     * @param safeTxHash    EIP-712 digest of the transaction.
     * @param signer        Signer address to be checked.
     */
    function _signerSignedTx(
        bytes32 safeTxHash,
        address signer
    ) internal view override returns (bool signed) {
        signed = _hasSignerSignedTx[safeTxHash][signer];
    }

    /**
     * @dev Internal function to store the transaction data and signature after validation.
     *
     * @param safeTxHash     EIP-712 digest of the transaction.
     * @param safeAddress    Target Safe Smart‑Account.
     * @param chainId        Chain id the transaction is meant for.
     * @param nonce          Safe nonce.
     * @param to             Destination of the inner call/delegatecall.
     * @param value          ETH value forwarded by the Safe.
     * @param data           Calldata executed by the Safe.
     * @param operation      0 = CALL, 1 = DELEGATECALL.
     * @param safeTxGas      Gas forwarded to the inner call.
     * @param baseGas        Fixed overhead reimbursed to the submitting signer.
     * @param gasPrice       Gas price used for reimbursement.
     * @param gasToken       ERC‑20 token address for refunds (`address(0)` = ETH).
     * @param refundReceiver Address receiving the gas refund.
     */
    function _storeTransaction(
        bytes32 safeTxHash,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        address to,
        uint256 value,
        bytes calldata data,
        uint8 operation,
        uint256 safeTxGas,
        uint256 baseGas,
        uint256 gasPrice,
        address gasToken,
        address refundReceiver
    ) internal override {
        // Store parameters only once (idempotent write)
        SafeTransaction storage slot = _txDetails[safeTxHash];
        if (!slot.stored) {
            // first encounter → persist full parameter set
            slot.stored = true;
            slot.to = to;
            slot.operation = operation;

            // Writing to storage is expensive, so we only write if the value is non-zero
            if (value > 0) {
                slot.value = CoreLib.safeCastUint256ToUint128(value);
            }
            if (safeTxGas > 0) {
                slot.safeTxGas = CoreLib.safeCastUint256ToUint128(safeTxGas);
            }
            if (baseGas > 0) {
                slot.baseGas = CoreLib.safeCastUint256ToUint128(baseGas);
            }
            if (gasPrice > 0) {
                slot.gasPrice = CoreLib.safeCastUint256ToUint128(gasPrice);
            }
            if (gasToken != address(0)) {
                slot.gasToken = gasToken;
            }
            if (refundReceiver != address(0)) {
                slot.refundReceiver = refundReceiver;
            }
            if (data.length > 0) {
                slot.data = data;
            }

            emit NewTransaction(
                safeTxHash,
                safeAddress,
                chainId,
                nonce,
                to,
                value,
                operation,
                safeTxGas,
                baseGas,
                gasPrice,
                gasToken,
                refundReceiver,
                data
            );
        }
    }

    /**
     * @dev Internal function to store a signature after validation.
     *
     * @param signer        Address that signed the transaction.
     * @param safeAddress   Target Safe Smart-Account.
     * @param chainId       Chain id the transaction is meant for.
     * @param nonce         Safe nonce.
     * @param safeTxHash    EIP-712 digest of the transaction.
     * @param r             First 32 bytes of the signature.
     * @param vs            Compact representation of s and v from EIP-2098.
     *
     * @return listIndex    Index of the stored signature in the signer-specific list.
     */
    function _storeSignature(
        address signer,
        address safeAddress,
        uint256 chainId,
        uint256 nonce,
        bytes32 safeTxHash,
        bytes32 r,
        bytes32 vs
    ) internal override returns (uint256 listIndex) {
        _hasSignerSignedTx[safeTxHash][signer] = true;

        SignatureDataWithTxHashIndex[] storage list = _sigData[signer][
            safeAddress
        ][chainId][nonce];
        listIndex = list.length;

        list.push(
            SignatureDataWithTxHashIndex({r: r, vs: vs, txHash: safeTxHash})
        );

        emit SignatureStored(
            signer,
            safeAddress,
            safeTxHash,
            chainId,
            nonce,
            listIndex
        );
    }
}

